class Solution {
    public List<String> letterCombinations(String digits) {

        List<String> result = new ArrayList<>();

        // If digits is empty, return the empty result
        if(digits.length() == 0){
            return result;
        }

        // Create a HashMap to store digit-letter mappings
        HashMap<String,String> digitToLetters = new HashMap<>();
        digitToLetters.put("2","abc"); digitToLetters.put("3","def"); digitToLetters.put("4","ghi");
        digitToLetters.put("5","jkl"); digitToLetters.put("6","mno"); digitToLetters.put("7","pqrs");
        digitToLetters.put("8","tuv"); digitToLetters.put("9","wxyz"); 

        // Create a queue to store intermediate combinations
        Queue<String> queue = new LinkedList<>();
        queue.add("");

        // Iterate over each digit in the input string
        for(int i = 0; i < digits.length(); i++){

            // Get the current digit and its corresponding letters
            String currentDigit = digits.substring(i,i+1);
            String letters = digitToLetters.get(currentDigit);

            // Get the current size of the queue
            int queueSize = queue.size();

            // For each combination in the queue, append each letter of the current digit and add it to the queue
            for(int j = 0; j < queueSize; j++){
                String currentCombination = queue.poll();
                for(int k = 0; k < letters.length();k++){
                    String newCombination = currentCombination.concat(letters.charAt(k)+"");
                    queue.add(newCombination);
                }
            }
        }

        // Convert the queue to a list and return the result
        while(!queue.isEmpty()){
            result.add(queue.poll());
        }
        return result;
    }
}

