        map.put('8', new Character[]{ 't', 'u', 'v' });
        map.put('9', new Character[]{ 'w', 'x', 'y', 'z' });

        char no[]= digits.toCharArray();//Converting the Sring to Character Array 
        dfs ( map, no, 0, "" );//Calling the DFS Function 
        return res;
    }
    public void dfs (HashMap<Character, Character[]> map, char no[], int phNo, String stringSoFar )
    {//Postorder Appraoch // Divide and Conquer

        if ( stringSoFar.length() == no.length  ){//When we found our desired String//CONQUER 
            res.add( stringSoFar );//Adding the new String to the 

            return;//Returning so that we can explore other combination letters of the digit 
        }
        for ( char ch: map.get(no[phNo]))//DIVIDE
             dfs ( map, no, phNo+1, stringSoFar+ ch);///Going in Deapth of the Recursion Tree // Like Neural Network 
        return;
    }
