function comb(map, dig, out=[], curr="", len){
    if(curr.length===len){
        out.push(curr);
        return [];
    }
    else{
        for(let i = 0; i<dig.length; i++){
            let ch = dig[i];
            let str = map[ch];
            let newDig = dig.slice(i+1);
            for(let j = 0; j<str.length; j++){
                comb(map, newDig, out, curr + str[j], len);
            }

        }
    }
    return out;
}
