func backtrack(curStr string, res *[]string, index int, digits string, digitsMap map[byte][]string) {
    if index == len(digits) {
        *res = append(*res, curStr)
        return
    }
    for _, ch := range digitsMap[digits[index]] {
        backtrack(curStr+ch, res, index+1, digits, digitsMap)
    }
}
