class Solution {
public:
    const vector<string> pad = {"", "", "abc", "def", "ghi", "jkl","mno", "pqrs", "tuv", "wxyz"};
    vector<string>ans;
    void recur(string digits, int ind,string &curr,int n){
        if(ind == n){
            ans.push_back(curr);
            return;
        }

        for(int i = 0; i< pad[digits[ind] - '0'].size(); i++){
            curr = curr + pad[digits[ind] - '0'][i];
            recur(digits,ind+1,curr,n);
            curr.pop_back();
        }
    }

    vector<string> letterCombinations(string digits) {
        if(digits.size() == 0)return {};
        int n = digits.size();

        string curr = "";
        recur(digits,0,curr,n);

        return ans;
    }
};
