class Solution {
    public List<String> letterCombinations(String digits) {
        List<String> ans = new ArrayList<>();
        if (digits.isEmpty()) {
		// if intial string is empty just return empty list
            return ans;
        }
		//if not call the helper function with 
		//processed and unprocessed strings as arguments
        ans = helper("", digits);
        return ans;
    }
    List<String> helper(String p, String up) {
	//create the outer list
        List<String> ans = new ArrayList<>();
        if (up.isEmpty()) {
		// if up gets empty in recursive call just returning the p in the list
            List<String> list = new ArrayList<>();
            list.add(p);
            return list;
        }
		//taking digit from string
        int digit = up.charAt(0) - '0';
        if (digit >= 2 && digit <= 6) {
		//loop for digits 2 to 6
            for (int i = (digit - 2) * 3; i <= ((digit - 1) * 3) - 1; i++) {
                char ch = (char) ('a' + i);
				//adding all the recursive answers in list
                ans.addAll(helper(p + ch, up.substring(1)));
            }
        } else if (digit == 7) {
		//for 7
            for (int i = (digit - 2) * 3; i < ((digit - 1) * 3) + 1; i++) {
                char ch = (char) ('a' + i);
					//adding all the recursive answers in list
                ans.addAll(helper(p + ch, up.substring(1)));
            }
        } else if (digit == 9) {
		//for 9
            for (int i = (digit - 2) * 3 + 1; i <= ((digit - 1) * 3) + 1; i++) {
                char ch = (char) ('a' + i);
					//adding all the recursive answers in list
                ans.addAll(helper(p + ch, up.substring(1)));
            }
        } else {
		//for 8
            for (int i = (digit - 2) * 3 + 1; i < ((digit - 1) * 3) + 1; i++) {
                char ch = (char) ('a' + i);
					//adding all the recursive answers in list
                ans.addAll(helper(p + ch, up.substring(1)));
            }
        }
		//finally returning the answer!!
        return ans;
    }
}
