 ` class Solution {
    String[] arr = {"00", "00", "abc", "def", "ghi", "jkl", "mno", "pqrs", "tuv", "wxyz"};

    public List<String> letterCombinations(String digits) {
        // Base case
        if (digits.length() == 0) {
            ArrayList<String> word = new ArrayList<>();
            word.add("");
            return word;
        }


        char ch1 = digits.charAt(0);
        String restCh = digits.substring(1);

        List<String> allnext = letterCombinations(restCh);
        List<String> thatlist = new ArrayList<>();

        String str = arr[ch1 - '0'];
        for (int i = 0; i < str.length(); i++) {
            char fstL = str.charAt(i);
            for (String lastL : allnext) { 
                thatlist.add(fstL + lastL);
            }
        }
        return thatlist;
    }
} `

