        if (digits.isEmpty()) {
            ArrayList<String> list = new ArrayList<>();
            list.add(p);
            return list;
        }
        ArrayList<String> list = new ArrayList<>();
        int digit = digits.charAt(0) - '0';
        if (digit >= 7) {
            for (int i = (digit - 2) * 3; i <= (digit - 1) * 3 ; i++) {
                char ch = (char) ('a' + i);
                list.addAll(helper(p + ch, digits.substring(1)));
            }
        } else {
            for (int i = (digit - 2) * 3; i < (digit - 1) * 3; i++) {
                char ch = (char) ('a' + i);
                list.addAll(helper(p + ch, digits.substring(1)));
            }
        }
        return list;
    }
}

