        if(digits.empty()) return res;
        unordered_map<char,string> hash{{'2',"abc"},{'3',"def"},{'4',"ghi"},{'5',"jkl"},{'6',"mno"},{'7',"pqrs"},{'8',"tuv"},{'9',"wxyz"}};
        string s;
        dfs(digits,0,s,hash,res);
        return res;
    }
    void dfs(string &digits,int idx,string s,unordered_map<char,string> &hash,vector<string> &res){
        if(idx==digits.size()){
            res.push_back(s);
            return;
        }
        for(auto c:hash[digits[idx]]){
            dfs(digits,idx+1,s+c,hash,res);
        }
    }
};

