class Solution:
    def possibleBipartition(self, n: int, dislikes: List[List[int]]) -> bool:
        color = [-1] * n
        graph = defaultdict(list)
        for a, b in dislikes:
            graph[a-1].append(b-1)
            graph[b-1].append(a-1)

        def dfs(u, c):
            color[u] = c
            for v in graph[u]:
                if color[v] != -1 and color[v] != 1 - c:  # Neighbor is already has color, but it's not our expection!
                    return False
                if color[v] == -1 and not dfs(v, 1 - c):
                    return False
            return True

        for i in range(n):
            if color[i] == -1 and not dfs(i, 0):
                return False

        return True
```
