                q.push(i);
                while(!q.empty()){
                    int node=q.front();
                    q.pop();
                    for(int child:adj[node]){
                        if(color[child]==color[node])return false;
                        if(!color[child]){
                            q.push(child);
                            color[child]=-1*color[node];
                        }
                    }
                }
            }
        }

        return true;
    }
};
