class Solution {
    unordered_map <int, int> color;
    vector<int> *adjlist;

public:
    bool possibleBipartition(int n, vector<vector<int>>& dislikes) {
        adjlist = new vector<int>[n+1];
        for(const vector<int> &d: dislikes) {
            adjlist[d[0]].emplace_back(d[1]);
            adjlist[d[1]].emplace_back(d[0]);
        }

        for(int i = 1; i <= n; i++) {
            queue<int> q;
            q.push(i);
            color.insert({i, 0});

            while(!q.empty()) {
                int node = q.front();
                q.pop();

                for(auto itr = adjlist[node].begin(); itr != adjlist[node].end(); itr++) {
                    // all neighbors of current node are given alternate color
                    int altColor = color[node]^1;
                    if(color.find(*itr) == color.end()) color.insert({*itr, altColor});
                    else if(color[*itr] == altColor)    continue;
                    else    return false;

                    q.push(*itr);
                }
            }
        }
        return true;
    }
};

