		color[node] = newcolor;
		for (auto i : adj[node])
		{
			if (color[i] == -1)
			{
				possibleToColor(i, 1 - newcolor, adj, color);
			}
			else if (color[i] == color[node])
			{
				return false;
			}
		}
		return true;
	}
