class UnionFind {
private:
    int N;
    vector<int> _parent;
    vector<int> _rank;
public:
    UnionFind(int n):
    N(move(n)) 
    {
        _parent = vector<int>(N);
        _rank = vector<int>(N, 0);

        for(int i = 0; i < N; i++)
            _parent[i] = i;
    }

    int find(int i) {
        if(_parent[i] != i) {
            _parent[i] = find(_parent[i]);
        }
        return _parent[i];
    }

    bool _union(const int& i, const int& j) {
        int parentI = find(i);
        int parentJ = find(j);

        if(parentI == parentJ)
            return false;

        if(_rank[parentI] < _rank[parentJ]) {
            _parent[parentI] = parentJ;
        }
        else if(_rank[parentI] > _rank[parentJ]) {
            _parent[parentJ] = parentI;
        }
        else {
            _parent[parentJ] = parentI;
            _rank[parentI]++;
        }
        return true;
    }
};

class Solution {
public:
    bool possibleBipartition(int N, vector<vector<int>>& dislikes) {
        unordered_map<int, vector<int>> _adjacencyMap;

        UnionFind uF(N+1);

        for(const auto& vec: dislikes) {
            _adjacencyMap[vec[0]].push_back(vec[1]); 
            _adjacencyMap[vec[1]].push_back(vec[0]); 
        }

        for(const auto& pair: _adjacencyMap) {
            const auto& list = pair.second;
            int first = list[0];
            for(int i = 1; i < list.size(); i++) {
                uF._union(first, list[i]);
            }
        }

        for(const auto& vec: dislikes) {
            if(uF.find(vec[0]) == uF.find(vec[1]))
                return false;
        }

        return true;
    }
};
