from collections import defaultdict
class Solution(object):
    def possibleBipartition(self, N, dislikes):
        """
        :type N: int
        :type dislikes: List[List[int]]
        :rtype: bool
        """
        dus = DisjointUnionSet(N)
        reject = defaultdict(list)
        for u, v in dislikes:
            reject[u].append(v)
            reject[v].append(u)

        for u, rivals in reject.items():
            if not rivals:
                continue
            for i, v in enumerate(rivals):
                if dus.find(u) == dus.find(v):
                    return False
                dus.merge(rivals[0], v)
        return True


class DisjointUnionSet(object):
    def __init__(self, n):
        # parents/rank index from 1
        self.parents = range(n + 1)
        self.rank = [1] * (n + 1)

    def find(self, u):
        if u == self.parents[u]:
            return u
        self.parents[u] = self.find(self.parents[u])
        return self.parents[u]

    def merge(self, u, v):
        pu, pv = self.find(u), self.find(v)
        ru, rv = self.rank[pu], self.rank[pv]
        if ru > rv:
            self.parents[pv] = pu
        elif ru < rv:
            self.parents[pu] = pv
        else:
            self.parents[pu] = pv
            self.rank[pv] += 1    
        return
