class UnionF {
	int *id,c,*sz;
public:
	UnionF(int N) {
		c=N;
		id=new int[N];
		sz=new int[N];
		for(int i=0; i<N; i++) {
			id[i]=i;
			sz[i]=1;
		}
	}
	~UnionF() {
		delete[] id;
		delete[] sz;
	}
	void clear(int N) {
		for(int i=0; i<N; i++) {
			id[i]=i;
			sz[i]=1;
		}
		c=N;
	}
	inline int find(int p) {
		int root=p;
		while(root!=id[root])root=id[root];
		while(p!=root) {
			int x=p;
			id[x]=root;
			p=id[p];
		}
		return root;
	}

	bool link(int p,int q) {
		int pid=find(p);
		int qid=find(q);
		if(qid==pid) return false;
		if(sz[pid]<sz[qid]) {
			id[pid]=qid;
			sz[qid]+=sz[pid];
		} else {
			id[qid]=pid;
			sz[pid]+=sz[qid];
		}
		--c;
		return true;
	}
	int count() {
		return c;
	}
	bool connected(int p,int q) {
		return find(p)==find(q);
	}
};

class Solution {
public:
    bool possibleBipartition(int N, vector<vector<int>>& dislikes) {
        int m=dislikes.size();
        if(m<2) return true;
        UnionF uf(N*2+1);
        for(auto &E: dislikes){
            uf.link(E[0],E[1]+N);
            uf.link(E[1],E[0]+N);
        }
        for(int i=1;i<=N;++i){
            if(uf.find(i)==uf.find(i+N)) return false;
        }
        return true;
    }
};
