	 class Solution {
    public boolean possibleBipartition(int N, int[][] dislikes) {
        int[] colors = new int[N + 1];
        Arrays.fill(colors, -1);
        int[][] graph = new int[N + 1][N + 1];

        for(int[] dislike : dislikes) {
            graph[dislike[0]][dislike[1]] = 1;
            graph[dislike[1]][dislike[0]] = 1;
        }
        for(int i = 1; i <= N; i++) {
            if(colors[i] == -1 && !isValid(graph, colors, 0, i)) {
                return false;
            }
        }

        return true;
    }

    private boolean isValid(int[][] graph, int[] colors, int color, int node) {
        if(colors[node] != -1) {
            return colors[node] == color;
        }

        colors[node] = color;

        for(int i = 1; i < graph[node].length; i++) {
            if(graph[node][i] == 1 && !isValid(graph, colors, 1 - color, i)) {
                return false;
            }
        }

        return true;
    }}

