class Solution:
    def possibleBipartition(self, n: int, dislikes: List[List[int]]) -> bool:
        # key: nodeNumber
        # value: array of neighbours
        graph: dict[int, List[int]] = dict()
        for d in dislikes:
            if d[0] not in graph:
                graph[d[0]] = []
            graph[d[0]].append(d[1])


        # key: node nodeNumber
        # value: group number (1 or -1)
        group = dict()

        def dfs(group, node, initGroup):
            stack = [(node, initGroup)]
            while len(stack) > 0:
                curNode, curGroup = stack.pop()
                otherGroupNumber = curGroup * -1
                if curNode not in group:
                    group[curNode] = curGroup

                    neighbours = graph.get(curNode) or []
                    for neighbour in neighbours:
                        stack.append((neighbour, otherGroupNumber))
                else:
                    if group[curNode] == -1 * curGroup:
                        # print("false", curNode, curGroup, group)
                        return False

            return True

        for node in range(1, n + 1):
            if node not in group:
                valid = dfs(group, node, node)
                if not valid:
                    # print(group)
                    return False


        # print(group)
        return True






