class Solution {
public:
    bool dfs(int node,int col,vector<int> & Color,vector<int> adjLs[]){ 
        Color[node] = col;
        for(auto it : adjLs[node]){
                if( Color[it] == -1){ // Not colored so unvisited so DFS is called for it
                   if( dfs(it,!col,Color,adjLs) == false) return false;
                }
                else if(Color[it]==col){ // Cannot have same color as the Adjacent node
                    return false;
                }
        }
       return true;
    }
    bool possibleBipartition(int n, vector<vector<int>>& dislikes) {
        //Initialise Color array
        vector<int> Color(n+1,-1);
        for(int i=0;i<=n;i++){
            Color[i] = -1;
        }
        //Converting into adjacency list
        vector<int> adjLs[n+1];
        for(auto it : dislikes ){
            adjLs[it[0]].push_back(it[1]);  
            adjLs[it[1]].push_back(it[0]);
        }

        for(int i=1;i<=n;i++){

               if(Color[i]==-1){ //Uncolored so call DFS               //1 | 2 |3 
