/**
 * @param {number} n
 * @param {number[][]} dislikes
 * @return {boolean}
 */
var possibleBipartition = function(n, dislikes) {
    if (!dislikes.length) {
        return true;
    }
    const g1 = new Set();
    const g2 = new Set();
    g1.add(dislikes[0][0]);
    g2.add(dislikes[0][1]);
    dislikes = dislikes.slice(1);
    while (dislikes.length) {
        const q = [];

        for (const [a, b] of dislikes) {
            if (g1.has(a) || g2.has(b)) {
                if (g1.has(b) || g2.has(a)) {
                    return false;
                }
                g1.add(a);
                g2.add(b);
            } else if (g2.has(a) || g1.has(b)) {
                if (g1.has(a) || g2.has(b)) {
                    return false;
                }
                g1.add(b);
                g2.add(a);
            } else {
                q.push([a, b]);
            }
        }
        if (q.length === dislikes.length) {
            g1.add(dislikes[0][0]);
            g2.add(dislikes[0][1]);
            dislikes = dislikes.slice(1);
        } else {
            dislikes = q;
        }
    }
    return true;
};
