class Solution {
public:
    bool bfs(unordered_map<int,vector<int>>& adj,vector<int>& color,int src)
    {
        queue<int> q;
        q.push(src);
        color[src]=0;

        while(!q.empty())
        {
            int front = q.front();
            q.pop();

            for(auto i: adj[front])
            {
                if(color[i]==-1)
                {
                    q.push(i);
                    color[i] = 1-color[front];
                }

                if(color[i]==color[front]) return false;
            }
        }
        return true;
