      if(flag==false){
            return;
      }

      if(color[node]==p_color){
        flag=false;
        return;
      }
      else if(color[node]==0)
        color[node]=-p_color;
      else return;

      if(flag)
      for(int i=0;i<v[node].size() && flag;i++){
        int n_node = v[node][i];
        if(color[n_node]==color[node]){
          flag=false;
          return;
        }
        else if(color[n_node]==0){
          dfs(v, color, n_node, -p_color);
