/* Union-find solution */
public boolean possibleBipartition(int n, int[][] dislikes) {
        int[] root = new int[n];
        for (int i = 0; i < root.length; i++) {
            root[i] = i;
        }

        LinkedList<Integer>[] graph = new LinkedList[n];

        for (int i = 0; i < n ; i++ ) {
            graph[i] = new LinkedList<Integer>();
        }

        for(int[] dislike : dislikes){
            int a = dislike[0] - 1;
            int b = dislike[1] - 1;
            graph[a].add(b);
            graph[b].add(a);
        }

        for(int i = 0; i < n; i++){
            LinkedList<Integer> adjList = graph[i];
            for(int adj : adjList) {
                int xRoot = find(i, root);
                int yRoot = find(adj, root);
                if(xRoot != yRoot){
                    root[find(adjList.get(0), root)] = yRoot;
                }else{
                    return false;
                }
            }
        }
        return true;
    }

    public int find(int x, int[] root){
        while(x != root[x]){
            root[x] = root[root[x]];
            x = root[x];
        }
        return x;
    }

	/* Graph coloring solution */
	public boolean possibleBipartition(int n, int[][] dislikes) {
        int[] colors = new int[n];
        LinkedList<Integer>[] graph = new LinkedList[n];

        for (int i = 0; i < n ; i++ ) {
            graph[i] = new LinkedList<>();
        }

        for(int[] dislike : dislikes){
            int a = dislike[0];
            int b = dislike[1];
            graph[a].add(b);
            graph[b].add(a);
        }

        for(int i = 0; i < n; i++ ){
            if(colors[i] != 0) continue;

            Queue<Integer> queue = new LinkedList<>();
            queue.offer(i);

            while(!queue.isEmpty()){
                int curr = queue.poll();
                colors[curr] = 1;
                for(int adj : graph[curr]){
                    if(colors[adj] == 0){
                        colors[adj] = -colors[curr];
                    }else if(colors[adj] != -colors[curr]){
                        return false;
                    }
                }
            }
        }
        return true;
    }
