class Solution {
public:
    bool possibleBipartition(int N, vector<vector<int>>& dislikes) {
        vector<int>color(N + 1, 0);
        vector<vector<int>>g(N+1);
        for(auto a:dislikes)
        {
            g[a[0]].push_back(a[1]);
            g[a[1]].push_back(a[0]);
        }
        for(int i = 1; i <= N; i++)
        {
            if(color[i] != 0)continue;
            color[i] = 1;
            queue<int>q{{i}};
            while(!q.empty())
            {
                auto t = q.front(); q.pop();
                for(auto a:g[t])
                {
                    if(color[a] == color[t])return false;
                    if(color[a] == 0)
                    {
                        color[a] = -1*color[t];
                        q.push(a);
                    }
                }
            }
        }
        return true;
    }
};
