class Solution {
    public boolean possibleBipartition(int N, int[][] dislikes) {
        int[] partition = new int[N];
        //construct a new data structure
        ArrayList<Integer>[] graph = new ArrayList[N];//[ArrayList, ...]
        for(int i = 0; i < N; i++)
            graph[i] = new ArrayList<>();
        //initialiazed
        for(int i = 0; i < dislikes.length; i++){
            graph[dislikes[i][0] - 1].add(dislikes[i][1] -1);
            graph[dislikes[i][1] - 1].add(dislikes[i][0] -1);
        }
        //Queue for BFS
        Queue<Integer> queue = new LinkedList<>();        
        for(int n = 0; n < N; n++){
            if(partition[n] == 0) {queue.offer(n); partition[n] = 1;}
            while(!queue.isEmpty()){
                int cur = queue.poll();
                for(int i: graph[cur]){
                    if(partition[i] == partition[cur]) return false;
                    if(partition[i] == 0){
                        partition[i] = -partition[cur];
                        queue.offer(i);
                    }
                }
            }
        }
        return true;
    }
}
