class UNF {
public:
    UNF(int N) {
        this->size = N;
        this->father = vector<int>(N + 1, 0);
        int i;
        for (i = 0 ; i <= N; i++) {
            father[i] = i;
        }
    }

    int find(int x) {
        if (father[x] == x) {
            return x;
        }
        father[x] = find(father[x]);
        return father[x];
    }

    int sameset(int x, int y) {
        return (find(x) == find(y));
    }

    void unionset(int x, int y) {
        int a = find(x);
        int b = find(y);
        father[b] = a;
    }


private:
    int size;
    vector<int> father;

};

class Solution {
public:
    bool possibleBipartition(int N, vector<vector<int>>& dislikes) {
        UNF s = UNF(2 * N);
        for (auto d : dislikes) {
            s.unionset(d[0], d[1] + N);
            s.unionset(d[1], d[0] + N);
            if (s.sameset(d[0], d[1])) {
                return false;
            }
        }
        return true;
    }
};
