class Solution {
public:
    map<int, int> UF, pre;
    int Find(int i){
        while(UF[i]!=i){
            i = UF[i];
        }
        return i;
    }

    bool possibleBipartition(int n, vector<vector<int>>& dislikes) {
        if(dislikes.size()==0) return true;

        for(auto &dislike:dislikes){
            // initialize root & leaf
            if(UF.find(dislike[0])==UF.end()){
                UF[dislike[0]] = dislike[0];
            }
            if(UF.find(dislike[1])==UF.end()){
                UF[dislike[1]] = dislike[1];
            }
            // if we seen dislike[1] before, set the root of UF[dislike[0]] to a different group than dislike[1]
            if(UF[dislike[0]] == dislike[0] && pre.find(dislike[1])!=pre.end()){
                UF[dislike[0]] = Find(pre[dislike[1]]);
            }
            // if we seen dislike[0] before, set the root of UF[dislike[1]] to a different group than dislike[0]
            if(UF[dislike[1]] == dislike[1] && pre.find(dislike[0])!=pre.end()){
                UF[dislike[1]] = Find(pre[dislike[0]]);
            }

            // find root of dislike
            int root1 = Find(dislike[0]);
            int root2 = Find(dislike[1]);

            // if root of dislikes are same, return false!!
            if(root1==root2){
                return false;
            }

            // record another group dislike belong to
            pre[dislike[0]] = dislike[1];
            pre[dislike[1]] = dislike[0];
        }
        return true;
    }
};
