func dfs(arr [][]int,s int,f bool, vis map[int]bool) bool{
    vis[s] = f
    for _,ele := range arr[s] {
        if _,ok := vis[ele]; !ok{
            if dfs(arr,ele,!f,vis) {
                return true
            }
        } else if f1,_ := vis[ele]; f1==f{
            return true
        }
    }
    return false
}
