    vis := make(map[int]bool)
    arr := make([][]int,n+1)
    for _,dis := range dislikes {
        var person1 int = dis[0]
        var person2 int = dis[1]
        arr[person1] = append(arr[person1],person2)
        arr[person2] = append(arr[person2],person1)
    }

    for i := 1; i<=n ; i++ {
        if _,ok := vis[i] ; !ok {
            if(dfs(arr,i,false,vis)){
                return false;
            }
        }
    }
    return true 
}
