class Solution {
public:
    bool isCycle(int src,vector <int> adj[],vector <int> &col){

        if(col[src]==-1){
            col[src]=1;
        }

        for(int x: adj[src]){
           if(col[x]==-1){
               col[x]=1-col[src];
               if(!isCycle(x,adj,col)){
                   return false;
               }
           }
           else if(col[x]==col[src]){
               return false;
           }
        }
        return true;
