	// BFS 
	public boolean possibleBipartition(int n, int[][] dislikes) {
        int[] visited = new int[n+1];

        // construct adjList
        List<Integer>[] adjList = new List[n+1];
        for(int i=1; i<=n; i++) {
            adjList[i] = new LinkedList<>();
        }

        for(int[] edge : dislikes) {
            adjList[edge[0]].add(edge[1]);
            adjList[edge[1]].add(edge[0]);
        }

        for(int vertex=1; vertex<=n; vertex++) {
            if(visited[vertex] == 0 && !bfs(vertex, adjList, visited)) {
                return false;
            }
        }
        return true;
    }

    private boolean bfs(int src, List<Integer>[] adjList, int[] visited) {
        Queue<Integer> queue = new LinkedList<>();
        queue.add(src);
        visited[src] = 1;

        while(!queue.isEmpty()) {
            int vertex = queue.poll();
            for(int neighbor : adjList[vertex]) {
                if(visited[neighbor] == 0) {
                    queue.add(neighbor);
                    visited[neighbor] = visited[vertex]+1;
                } else if(visited[neighbor] == visited[vertex]) {
                    // detect cross edge and an odd length cycle
					// A cross edge betwee two vertices wich are of same distace from soruce vertex forms an odd length cycle
                    return false;
                }
            }
        }
        return true;
    }

	// DFS 
    public boolean possibleBipartition(int n, int[][] dislikes) {
        int[] visited = new int[n+1];

        // pre-process/ construct the graph
        List<Integer>[] adjList = new List[n+1];
        for(int vertex=1; vertex<=n; vertex++) {
            adjList[vertex] = new LinkedList<>();
        }

        for(int[] edge : dislikes) {
            adjList[edge[0]].add(edge[1]);
            adjList[edge[1]].add(edge[0]);
        }

        for(int vertex=1; vertex<=n; vertex++) {
            if(visited[vertex] == 0 && !dfs(vertex, adjList, 0, visited)) {
                return false;
            }
        }
        return true;
    }

    private boolean dfs(int vertex, List<Integer>[] adjList, int distanceFromSrc, int[] visited) {
        visited[vertex] = distanceFromSrc;
        for(int neighbor : adjList[vertex]) {
            if(visited[neighbor] == 0) {
                if(!dfs(neighbor, adjList, distanceFromSrc+1, visited)) {
                    return false;
                }
            } else if((visited[vertex]-visited[neighbor]+1)%2 == 1){
                // detected a back edge and an odd legth cycle
				// A back edge is dected first from a descedent to its ancestor for the first time,
				// at this point we check the difference in depth and add 1 to the lenght of the cycle
				// if the cycle lenght is odd return false
                return false;
            }
        }
        return true;
    }

