class DSU:
    def __init__(self, n):
        self.root = list(range(n))

    def find(self, x):
        if self.root[x] != x:
            self.root[x] = self.find(self.root[x])
        return self.root[x]

    def union(self, x, y):
        self.root[self.find(x)] = self.find(y)

class Solution:
    def possibleBipartition(self, n: int, dislikes: List[List[int]]) -> bool:
        #build graph, mapping x to a set of disliked people
        #union find each disliked people for each person, with the first person's root as root
        #check if there is contradict between the person x and disliked people

        dsu = DSU(n + 1)

        graph = defaultdict(list)

        for x, y in dislikes:
            graph[x].append(y)
            graph[y].append(x)

        for i in graph:
            group = graph[i]
            y = group[0]
            for x in group:
                dsu.union(x, y)
            if dsu.find(i) == dsu.find(y):
                return False

        return True


