from collections import defaultdict
from queue import Queue
class Solution:
    def possibleBipartition(self, n: int, dislikes: List[List[int]]) -> bool:
        # Create adjecency list 
        adj = defaultdict(list)
        for a, b in dislikes:
            adj[a].append(b)
            adj[b].append(a)

        # Check if graph is Bipartite graph
        colors = [-1]*(n+1)
        for i in range(1, n):
            if colors[i] == -1:
                # check if component with this node is bipartite graph
                if not self.isBipartite(i, adj, colors):
                    return False
        return True

    def isBipartite(self, v: int, adj: dict, colors: list) -> bool:
        colors[v] = 0
        neighbour_queue = Queue()
        neighbour_queue.put(v)
        while not neighbour_queue.empty():
            curr = neighbour_queue.get()
            for neighbour in adj[curr]:
                # Odd cycle
                if colors[neighbour] == colors[curr]:
                    return False
                # Unvisited 
                if colors[neighbour] == -1:
                    colors[neighbour] = 1 - colors[curr]
                    neighbour_queue.put(neighbour)
        return True

