                    continue;
                }

                vis[rvtx] = color;
                for (int e : graph[rvtx]) {
                    if (vis[e] == -1) {
                        que.addLast(e);
                    }
                }
            }
            color = (color + 1) % 2;
            if(!isBipartite) break;
        }



        return isBipartite;
    }
}

