class Solution {
public:
    bool isBipartite(vector<int>adj[],vector<int>&color,int node,int n){
        queue<int>q;
        q.push(node);
        color[node]=1;
        while(q.size()){
            int curr=q.front();
            q.pop();
            for(int ele:adj[curr]){
                if(color[curr]==color[ele])return false;
                if(color[ele]==-1){
                    color[ele]=1-color[curr];
                    q.push(ele);
                }
            }
        }
        return true;
