```
class Solution {
public:
    bool possibleBipartition(int N, vector<vector<int>>& dislikes) {
        vector<int>color(N+1,0);
        vector<vector<int>>adj(N+1);
        for(auto e:dislikes){
            adj[e[0]].push_back(e[1]);
            adj[e[1]].push_back(e[0]);
        }
        queue<int>q;
        for(int i=1;i<=N;i++){
            if(color[i]==0){
                q.push(i);
                color[i]=1;
            }
            else continue;
            while(!q.empty()){
                int u=q.front();q.pop();
                for(int i=0;i<adj[u].size();i++){
                    if(color[adj[u][i]]==0){
                        q.push(adj[u][i]);
                        color[adj[u][i]]=~color[u];
                    }
                    else if(color[u]==color[adj[u][i]]){
                        return false;
                    }
                }
            }
        }
        return true;
    }
};
```

