class Solution {
public:
    bool possibleBipartition(int N, vector<vector<int>>& dislikes) {
        vector<int>color(N+1,0);
        vector<vector<int>>adj(N+1);
        for(auto e:dislikes){
            adj[e[0]].push_back(e[1]);
            adj[e[1]].push_back(e[0]);
        }
        for(int i=1;i<=N;i++){
            if(color[i]==0){
                if(!dfs(i,color,adj,-1)) return false;
            }
        }
        return true;
    }
    bool dfs(int u,vector<int>&color,vector<vector<int>>&adj,int par){
        if(par==-1) color[u]=1;
        else color[u]=~color[par];
        for(int nei:adj[u]){
            if(nei==par) continue;
            if(color[nei]==color[u]) return false;
            else if(color[nei]==0){
                if(!dfs(nei,color,adj,u)) return false;
            }
        }
        return true;
    }
};
