class Solution {
    List<Integer> [] adj=null;
    public boolean possibleBipartition(int N, int[][] dislikes) {
        adj=new ArrayList[N];
        for(int i=0;i<N;i++)
        {
            adj[i]=new ArrayList<>();
        }
        //construct an adjacency list representation of undirected graph
        for(int [] arr:dislikes)
        {
            adj[arr[0]-1].add(arr[1]-1);// i have used zero based indexing
            adj[arr[1]-1].add(arr[0]-1);
        }

        //construction ends
        int [] colors=new int[N];
        Arrays.fill(colors,-1);
        for(int i=0;i<N;i++)
        {
            //for all node whose colour has not been assigned yet
           if(colors[i]==-1)
           {
            if(!util(colors,i))
                return false;
           }
        }
        return true;
    }
    boolean util(int [] colors,int src)
    {
        Queue<Integer> q=new LinkedList<>();
        q.add(src);
        while(!q.isEmpty())
        {
            Integer curr=q.poll();
            for(Integer nei:adj[curr])
            {
                //if neigbour node is not processed yet
                if(colors[nei]==-1 )
                {
                    colors[nei]=1-colors[curr];// this ensures that a different colour will be assigned to neighbour
                    q.add(nei);
                }
                // if prcossed and neighbour has same colour as that of parent return false
                else if(colors[nei]==colors[curr])
                {
                    return false;
                }
            }
        }
        return true;
    }
}
