class DisjointSet {
private:    
    struct Node {
        int key;
        int rank;
        Node* parent;

        Node(int key){
            this->key = key;
            rank = 0;
            parent = this;
        }
    };

    unordered_map<int, Node*> keyToNode;

public:

    ~DisjointSet() {
        for(auto it = keyToNode.begin(); it != keyToNode.end(); ++it){
            delete it->second;
        }
    }

    void makeSet(int key){
        if(keyToNode.find(key) != keyToNode.end()){
            return;
        }
        keyToNode[key] = new Node(key);
    }

    int findSet(int key){
        return findSet(keyToNode[key])->key;
    }

    bool unionSets(int key1, int key2){
        Node* node1 = keyToNode[key1];
        Node* node2 = keyToNode[key2];

        Node* rep1 = findSet(node1);
        Node* rep2 = findSet(node2);

        if(rep1 == rep2) {
            return false;
        }

        if(rep1->rank > rep2->rank){
            rep2->parent = rep1;
        } else if (rep1->rank < rep2->rank){
            rep1->parent = rep2;
        } else {
            rep2->parent = rep1;
            rep1->rank++;
        }
        return true;
    }

private:
    Node* findSet(Node* node){
        if(node->parent == node){
            return node;
        }
        // path compression
        node->parent = findSet(node->parent);
        return node->parent;
    }
};
```

