        vector<vector<int>> graph(n + 1);
        for (const auto& dislike : dislikes) {
            graph[dislike[0]].emplace_back(dislike[1]);
            graph[dislike[1]].emplace_back(dislike[0]);
        }

        vector<bool> visited(n + 1, false);
        vector<int> groups(n + 1, 0);

        // Iterate through all people
        for (int i = 1; i <= n; ++i) {
            // We only need to process people once
            if (!visited[i]) {
                // Assign to any group
                groups[i] = kGroup1;

                queue<int> to_process;
                to_process.emplace(i);
                // Breadth-first search
                while (!to_process.empty()) {
                    auto person = to_process.front();
                    to_process.pop();

                    if (visited[person]) continue;
                    visited[person] = true;

                    // Iterate through adjacent people
                    // (all the people this person dislike)
                    for (auto next_person : graph[person]) {
                        // If they are in the same group, it's
                        // not possible to split the graph into two
                        if (groups[person] == groups[next_person]) {
                            return false;
                        }
                        // Otherwise, assign them to the opposite group
                        groups[next_person] = groups[person] == kGroup1 ? kGroup2 : kGroup1;
                        to_process.emplace(next_person);
                    }
                }
            }
        }
        return true;
    }
};
