                        if (side[u] == -1) 
                        {
                            side[u] = side[v]^1;
                            q.push(u);
                        } 
                        else 
                            is_bipartite &= side[u] != side[v];
                    }
                }
            }
        }
        return is_bipartite;
    }
};

