bool possibleBipartition(int n, const std::vector<std::vector<int>>& dislikes) {
  using graph = std::vector<std::vector<int>>;
  graph g(n);
  for (const auto& vec : dislikes) {
    g[vec[0] - 1].push_back(vec[1] - 1);
    g[vec[1] - 1].push_back(vec[0] - 1);
  }

  enum class Color { red, black, not_set };
  std::vector<Color> colors(n, Color::not_set);
  std::vector<char> processed(n, 0);
  std::function<bool(int, Color)> dfs;
  dfs = [&](int to, Color color) {
    processed[to] = true;
    colors[to] = color;
    const auto new_color = color == Color::black ? Color::red : Color::black;
    for (int v : g[to]) {
      if (colors[v] != Color::not_set && colors[v] != new_color)
        return false;
      if (!processed[v] && !dfs(v, new_color))
        return false;
    }
    return true;
  };

  for (int i = 0; i < n; ++i) {
    if (!processed[i] && !dfs(i, Color::red))
      return false;
  }
  return true;
}

