var possibleBipartition = function(N, dislikes) {
  let map = {};
  let group = new Array(N).fill(0);

  for (let [a, b] of dislikes) {
    if (map.hasOwnProperty(a)) {
      map[a].push(b);
    } else {
      map[a] = [b];
