```csharp
public class Solution {
    enum Colour { Uncoloured, Red, Blue }

    public bool PossibleBipartition(int N, int[][] dislikes) 
    {
        //Given a graph, can we colour all nodes: {red, blue} s.t all red neighbours are blue
        //and vice-versa
        var graphColour = Enumerable.Range(1,N).ToDictionary(x => x, x => Colour.Uncoloured);

        //rearrange the dislikes Array. 
        //[[1,2],[1,3],[2,4]] =>
        //dislikeLookup[1]: [2,3]
        //dislikeLookup[2]: [1,4]
        //dislikeLookup[3]: [1]
        //dislikeLookup[4]: [2]
        var dislikeLookup = dislikes.Concat(dislikes.Select(x => new[] { x[1], x[0] })).ToLookup(x => x[0], x => x[1]);


        IEnumerable<int> GetDislikes(int k) => dislikeLookup[k];
        Colour SetColour(int i, Colour colour) => graphColour[i] = colour;
        Colour GetColour(int i) => graphColour[i];
        bool IsColoured(int i) => GetColour(i) != Colour.Uncoloured;
        Colour Swap(Colour colour) => colour == Colour.Blue ? Colour.Red : Colour.Blue;

        bool DFS(int k, Colour colour)
        {
            //By performing DFS, we will have coloured all nodes in the connected component from some root k.
            //Therefore if we reached a node previously, we've already considered all of its neighbours and it was successful.
			//Note: At this point, we may be considred a root, so the vaue of colour does not matter.
            if(IsColoured(k)) { return true; }

            SetColour(k, colour);
            foreach(var d in GetDislikes(k))
            {
                if(GetColour(d) == colour)
                {
                    //We've already coloured a neighbour and it's the wrong colour, 
                    //We can't do a bipartite colouring
                    return false;
                }

                //Valid colouring already performed, try the next one.
                if(GetColour(d) == Swap(colour)) { continue; }

                //We haven't coloured d, it can only be the opposite colour of the current node.
                //Try a colouring, if it fails we can't backtrack, so fail the procedure.
				//Note that if we're successful, we want to continue with the next dislike, not return true.
                if(!DFS(d, Swap(colour))) { return false; }
            }
            //All colourings were successful
            return true;
        }

        //If we have a fully connected graph, we only need to colour once from a 'root'
        //We likely don't have a root, so we work through each connected component starting from an arbitrary root.
		//DFS will terminate quickly if the root has already been coloured.
        foreach(var k in graphColour.Keys.ToList())
        {
            if(!DFS(k, Colour.Blue)) { return false; }
        }
        return true;
    }
}
