//BFS 
    //Time: O(E + N) E:num(edges) N:num(people)
    public boolean possibleBipartition(int N, int[][] dislikes) {
        //build graph
        List<Integer>[] graph = new ArrayList[N + 1];
        for(int i = 0; i <= N; i++){
            graph[i] = new ArrayList<>();
        }
        for(int i = 0; i < dislikes.length; i++){
            graph[dislikes[i][0]].add(dislikes[i][1]);
            graph[dislikes[i][1]].add(dislikes[i][0]);
        }
        int[] color = new int[N + 1];
        Queue<Integer> q = new LinkedList<>();
        //BFS, add all people to two groups
        for(int i = 1; i <= N; i++){
            if(color[i] != 0) continue; //human already added to one group
            color[i] = 1;//assign human to a group
            q.add(i);
            while(!q.isEmpty()){
                int human = q.poll();
                for(int hater : graph[human]){
                    if(color[hater] == 0){// hater not added to any group
                        q.add(hater);
                        color[hater] = -color[human];
                    }else if(color[hater] + color[human] != 0){ //haters belong to same group
                        return false;
                    }
                }
            }
        }  
        return true;
    }
