        return a;
    }
    par[a] = find(par[a],par);
    return par[a];
}
void Union(int a,int b,int size[],int par[]){
    a = find(a,par);
    b = find(b,par);
    if(a!=b){
        if(size[a]<size[b]){
            swap(a,b);
        }
        size[a]+=size[b];
        par[b] = a;
