We can use the same approach of bipartite graph and apply BFS or DFS

# BFS Solution
```
class Solution {
public:
    bool bfs(vector<int> adj[],int node,vector<int> &color,int currColor){
        color[node]=currColor;
        queue<int> q;
        q.push(node);
        while(!q.empty()){
            int currNode=q.front();
            q.pop();
            for(auto adjNode:adj[currNode]){
                if(color[adjNode]==-1){
                    q.push(adjNode);
                    color[adjNode]=!color[currNode];
                }
                else if(color[adjNode]==color[currNode]){
                    return false;
                }
            }
        }
        return true;
    }
    bool possibleBipartition(int n, vector<vector<int>>& dislikes) {
        // Creating Graph
        vector<int> adj[n+1];
        for(auto dislike:dislikes){
            adj[dislike[0]].push_back(dislike[1]);
            adj[dislike[1]].push_back(dislike[0]);
        }
        vector<int> color(n+1,-1);
        for(int i=0;i<=n;i++){
            if(color[i]==-1){
                if(bfs(adj,i,color,0)==false){
                    return false;
                }
            }
        }
        return true;
    }
};
```
