impl Solution {
    pub fn subtract_product_and_sum(n: i32) -> i32 {
        let (p, s) = std::iter::successors(Some(n), |&x| Some(x/10))
            .take_while(|&x| x > 0)
            .map(|x| x%10)
            .fold((1, 0), |(p, s), x| (p*x, s+x));
        p - s
    }
}
