class Solution {
public:
    //find product of digits using recursion
    int product(int n){
        //base case
        if(n<10){
            return n;
        }
        //recursive case
        int smallProduct = product(n/10);
        return (n%10)*smallProduct;
    }

    //find sum of digits using recursion
    int sum(int n){
        //base case
        if(n<10){
            return n;
        }
        //recursive case
        int smallSum = sum(n/10);
        return (n%10)+smallSum;
    }

    int subtractProductAndSum(int n) {
        int temp = product(n);
        int temp1 = sum(n);
        return temp-temp1;
    }
};
