```class Solution {
public:
    int subtractProductAndSum(int n) {
        int sum = 0, pro = 1,diff;                     // The variable sum for the sum of all digit and pro for the product of all digit of number
        while(n>0)                                     // Run the loop until it becomes less than 0
        {
            int rem = n % 10;                          // find the last digit
            sum = sum + rem;                           // update sum and pro
            pro = pro * rem;
            n = n / 10;                                // update n
        }

        diff = pro - sum;                              // calculate difference

        return diff;                                   // return difference
    }
};

